import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css';

const typeColors = {
  normal: "#A8A77A",
  fire: "#EE8130",
  water: "#6390F0",
  electric: "#F7D02C",
  grass: "#7AC74C",
  ice: "#96D9D6",
  fighting: "#C22E28",
  poison: "#A33EA1",
  ground: "#E2BF65",
  flying: "#A98FF3",
  psychic: "#F95587",
  bug: "#A6B91A",
  rock: "#B6A136",
  ghost: "#735797",
  dragon: "#6F35FC",
  dark: "#705746",
  steel: "#B7B7CE",
  fairy: "#D685AD" 
};

function App() {
  const [pokemons, setPokemons] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [selectedPokemon, setSelectedPokemon] = useState(null);
  const [modalOpen, setModalOpen] = useState(false);
  const [loadingDetails, setLoadingDetails] = useState(false);
  const pokemonsPerPage = 20;

  useEffect(() => {
    async function fetchData() {
      const response = await axios.get('https://pokeapi.co/api/v2/pokemon?limit=151');
      const results = response.data.results;

      const detailed = await Promise.all(results.map(async (p) => {
        const res = await axios.get(p.url);
        return {
          name: res.data.name,
          image: res.data.sprites.front_default,
          types: res.data.types.map(t => t.type.name),
          url: p.url, // guardando url para buscar detalhes depois
        };
      }));

      setPokemons(detailed);
    }

    fetchData();
  }, []);

  // Paginação
  const indexOfLast = currentPage * pokemonsPerPage;
  const indexOfFirst = indexOfLast - pokemonsPerPage;
  const currentPokemons = pokemons.slice(indexOfFirst, indexOfLast);
  const totalPages = Math.ceil(pokemons.length / pokemonsPerPage);

  const goToNextPage = () => setCurrentPage((page) => Math.min(page + 1, totalPages));
  const goToPrevPage = () => setCurrentPage((page) => Math.max(page - 1, 1));

  // Função ao clicar num Pokémon
  const handleCardClick = async (url) => {
    setLoadingDetails(true);
    setModalOpen(true);
    try {
      const res = await axios.get(url);
      setSelectedPokemon(res.data);
      setLoadingDetails(false);
    } catch {
      setSelectedPokemon(null);
      setLoadingDetails(false);
    }
  };

  const handleModalClose = () => {
    setModalOpen(false);
    setSelectedPokemon(null);
  };

  return (
    <div className="App">
      <h1>Pokédex</h1>
      <div className="pokedex">
        {currentPokemons.map((pokemon, index) => (
          <div
            key={index}
            className="card"
            style={{ cursor: 'pointer' }}
            onClick={() => handleCardClick(pokemon.url)}
            title="Clique para ver detalhes"
          >
            <img src={pokemon.image} alt={pokemon.name} />
            <h3>{pokemon.name}</h3>
            <div style={{ display: 'flex', justifyContent: 'center', gap: '0.4rem', marginTop: '6px' }}>
              {pokemon.types.map((type) => (
                <p
                  key={type}
                  style={{
                    backgroundColor: typeColors[type],
                    color: "#fff",
                    borderRadius: "10px",
                    padding: "0.2rem 0.7rem",
                    margin: 0,
                    fontWeight: "bold",
                    textShadow: "0 1px 2px rgba(30,30,30,0.2)"
                  }}>
                  {type}
                </p>
              ))}
            </div>
          </div>
        ))}
      </div>
      {/* Paginação */}
      <div style={{ marginTop: '20px', display: 'flex', justifyContent: 'center', gap: '10px' }}>
        <button onClick={goToPrevPage} disabled={currentPage === 1}>
          Anterior
        </button>
        <span>Página {currentPage} de {totalPages}</span>
        <button onClick={goToNextPage} disabled={currentPage === totalPages}>
          Próxima
        </button>
      </div>

      {/* Modal de detalhes */}
      {modalOpen && (
        <div style={{
            position: 'fixed',
            top: 0, left: 0, right: 0, bottom: 0,
            background: 'rgba(0,0,0,0.5)',
            display: 'flex', justifyContent: 'center', alignItems: 'center'
        }}>
          <div style={{
              background: '#fff',
              padding: '2rem',
              borderRadius: '1rem',
              minWidth: '320px',
              maxWidth: '90vw',
              boxShadow: '0 10px 40px rgba(60,0,0,0.23)',
              position: 'relative',
              textAlign: 'center'
          }}>
            <button
              style={{
                position: 'absolute', top: 15, right: 15,
                background: '#d32f2f', color: '#fff',
                border: 'none', borderRadius: '50%',
                width: 32, height: 32, fontWeight: 'bold', fontSize: 18,
                cursor: 'pointer', boxShadow: '0 1px 2px #2224'
              }}
              onClick={handleModalClose}
              title="Fechar"
            >×</button>
            {loadingDetails ? (
              <p>Carregando...</p>
            ) : selectedPokemon ? (
              <>
                <img
                  src={selectedPokemon.sprites.front_default}
                  alt={selectedPokemon.name}
                  style={{ width: 110, height: 110, marginBottom: 16, background: "#e0e0e0", borderRadius: "50%", border: "2px solid #1976d2" }}
                />
                <h2 style={{ textTransform: 'capitalize', color: "#d32f2f" }}>
                  {selectedPokemon.name} <span style={{fontSize: "1rem", fontWeight: "bold", color: "#424242"}}>#{selectedPokemon.id}</span>
                </h2>
                <div style={{ display: 'flex', justifyContent: 'center', gap: '0.4rem', marginBottom: 12 }}>
                  {selectedPokemon.types.map(t => (
                    <p key={t.type.name}
                       style={{
                         backgroundColor: typeColors[t.type.name],
                         color: "#fff",
                         borderRadius: "10px",
                         padding: "0.2rem 0.7rem",
                         margin: 0,
                         fontWeight: "bold",
                         textShadow: "0 1px 2px rgba(30,30,30,0.2)"
                       }}>
                      {t.type.name}
                    </p>
                  ))}
                </div>
                <div style={{ textAlign: 'left', margin: "0 auto", maxWidth: "270px" }}>
                  <strong>Peso:</strong> {selectedPokemon.weight / 10} kg<br />
                  <strong>Altura:</strong> {selectedPokemon.height / 10} m<br />
                  <strong>Habilidades:</strong>
                  <ul>
                    {selectedPokemon.abilities.map(a => (
                      <li key={a.ability.name}>{a.ability.name}</li>
                    ))}
                  </ul>
                  <strong>Base Stats:</strong>
                  <ul>
                    {selectedPokemon.stats.map(stat => (
                      <li key={stat.stat.name}>
                        {stat.stat.name}: {stat.base_stat}
                      </li>
                    ))}
                  </ul>
                </div>
              </>
            ) : (
              <p>Erro ao carregar informações do Pokémon.</p>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
