import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css';

const typeColors = {
  normal: "#A8A77A",
  fire: "#EE8130",
  water: "#6390F0",
  electric: "#F7D02C",
  grass: "#7AC74C",
  ice: "#96D9D6",
  fighting: "#C22E28",
  poison: "#A33EA1",
  ground: "#E2BF65",
  flying: "#A98FF3",
  psychic: "#F95587",
  bug: "#A6B91A",
  rock: "#B6A136",
  ghost: "#735797",
  dragon: "#6F35FC",
  dark: "#705746",
  steel: "#B7B7CE",
  fairy: "#D685AD",
};

function App() {
  const [pokemons, setPokemons] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [selectedPokemon, setSelectedPokemon] = useState(null);
  const [modalOpen, setModalOpen] = useState(false);
  const [loadingDetails, setLoadingDetails] = useState(false);
  const pokemonsPerPage = 20;

  useEffect(() => {
    async function fetchData() {
      const response = await axios.get('https://pokeapi.co/api/v2/pokemon?limit=151');
      const results = response.data.results;
      const detailed = await Promise.all(results.map(async (p) => {
        const res = await axios.get(p.url);
        return {
          name: res.data.name,
          image: res.data.sprites.front_default,
          types: res.data.types.map(t => t.type.name),
          url: p.url,
        };
      }));
      setPokemons(detailed);
    }
    fetchData();
  }, []);

  const indexOfLast = currentPage * pokemonsPerPage;
  const indexOfFirst = indexOfLast - pokemonsPerPage;
  const currentPokemons = pokemons.slice(indexOfFirst, indexOfLast);
  const totalPages = Math.ceil(pokemons.length / pokemonsPerPage);

  const goToNextPage = () => setCurrentPage(page => Math.min(page + 1, totalPages));
  const goToPrevPage = () => setCurrentPage(page => Math.max(page - 1, 1));

  const handleCardClick = async (url) => {
    setLoadingDetails(true);
    setModalOpen(true);
    try {
      const res = await axios.get(url);
      setSelectedPokemon(res.data);
      setLoadingDetails(false);
    } catch {
      setSelectedPokemon(null);
      setLoadingDetails(false);
    }
  };

  const handleModalClose = () => {
    setModalOpen(false);
    setSelectedPokemon(null);
  };

  return (
    <div className="App">
      <h1>Pokédex</h1>
      <div className="pokedex">
        {currentPokemons.map((pokemon, index) => (
          <div
            key={index}
            className="card"
            style={{ cursor: 'pointer' }}
            onClick={() => handleCardClick(pokemon.url)}
            title="Clique para ver detalhes"
          >
            <img src={pokemon.image} alt={pokemon.name} />
            <h3>{pokemon.name}</h3>
            <div style={{ display: 'flex', justifyContent: 'center', gap: '0.4rem', marginTop: '6px' }}>
              {pokemon.types.map((type) => (
                <p
                  key={type}
                  style={{
                    backgroundColor: typeColors[type],
                    color: "#fff",
                    borderRadius: "10px",
                    padding: "0.2rem 0.7rem",
                    margin: 0,
                    fontWeight: "bold",
                    textShadow: "0 1px 2px rgba(30,30,30,0.2)"
                  }}>
                  {type}
                </p>
              ))}
            </div>
          </div>
        ))}
      </div>
      {/* Paginação */}
      <div style={{ marginTop: '20px', display: 'flex', justifyContent: 'center', gap: '10px' }}>
        <button onClick={goToPrevPage} disabled={currentPage === 1}>
          Anterior
        </button>
        <span>Página {currentPage} de {totalPages}</span>
        <button onClick={goToNextPage} disabled={currentPage === totalPages}>
          Próxima
        </button>
      </div>

      {/* Modal de detalhes */}
      {modalOpen && (
        <div
          style={{
            position: 'fixed',
            top: 0, left: 0, right: 0, bottom: 0,
            background: 'rgba(50,30,30,0.65)',
            zIndex: 1000,
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
          }}
        >
          <div
            style={{
              background: 'linear-gradient(135deg, #fff 85%, #e3e3e3 100%)',
              borderRadius: '1.3rem 2.3rem 1.3rem 2.3rem',
              boxShadow: '0 14px 36px 0 rgba(80,0,0,0.30)',
              minWidth: 320,
              maxWidth: 400,
              width: '96vw',
              padding: '2.2rem 1.5rem 1.7rem 1.5rem',
              position: 'relative',
              textAlign: 'center',
              border: '6px solid #1976d2'
            }}
          >
            <button
              style={{
                position: 'absolute',
                top: 10,
                right: 13,
                background: '#d32f2f',
                color: '#fff',
                border: 'none',
                borderRadius: '50%',
                width: 36,
                height: 36,
                fontWeight: 'bold',
                fontSize: 22,
                cursor: 'pointer',
                boxShadow: '0 2px 7px #21212125',
                transition: 'background 0.17s'
              }}
              onClick={handleModalClose}
              title="Fechar"
            >×</button>

            {loadingDetails ? (
              <p style={{fontWeight:"bold"}}>Carregando...</p>
            ) : selectedPokemon ? (
              <>
                <img
                  src={selectedPokemon.sprites.front_default}
                  alt={selectedPokemon.name}
                  style={{
                    width: 120,
                    height: 120,
                    marginBottom: 18,
                    background: "#c7dfff",
                    borderRadius: "50%",
                    border: "3px solid #1976d2",
                    boxShadow: "0 2px 12px #1976d233, 0 0 0 7px #fff inset"
                  }}
                />
                <h2 style={{
                  textTransform: 'capitalize',
                  color: "#d32f2f",
                  marginBottom: '0.3rem',
                  fontWeight: 900,
                  fontSize: '2rem',
                  letterSpacing: '1.5px',
                  textShadow: "1.5px 1.5px 0 #fff"
                }}>
                  {selectedPokemon.name}
                  <span style={{fontSize: "1.1rem", fontWeight: "bold", color: "#1976d2", marginLeft: 7}}>#{selectedPokemon.id}</span>
                </h2>
                <div style={{ display: 'flex', justifyContent: 'center', gap: '0.4rem', marginBottom: 18 }}>
                  {selectedPokemon.types.map(t => (
                    <p key={t.type.name}
                      style={{
                        backgroundColor: typeColors[t.type.name],
                        color: "#fff",
                        borderRadius: "12px",
                        padding: "0.19rem 0.8rem",
                        margin: 0,
                        fontWeight: "bold",
                        fontSize: "1rem",
                        textShadow: "0 1px 2px rgba(30,30,30,0.2)"
                      }}>
                        {t.type.name}
                    </p>
                  ))}
                </div>
                <div style={{
                  textAlign: 'left',
                  margin: "0 auto",
                  maxWidth: "270px",
                  background: "#f3f6fb",
                  border: "1.6px solid #d32f2f",
                  borderRadius: "0.9rem",
                  padding: '1rem 1.2rem',
                  boxShadow: '0 2px 9px #d32f2f23'
                }}>
                  <strong>Peso:</strong> {selectedPokemon.weight / 10} kg<br/>
                  <strong>Altura:</strong> {selectedPokemon.height / 10} m<br />
                  <strong>Habilidades:</strong>
                  <ul style={{marginTop:6,marginBottom:10}}>
                    {selectedPokemon.abilities.map(a => (
                      <li key={a.ability.name} style={{textTransform: "capitalize"}}>{a.ability.name}</li>
                    ))}
                  </ul>
                  <strong>Status base:</strong>
                  <ul style={{marginTop:6}}>
                    {selectedPokemon.stats.map(stat => (
                      <li key={stat.stat.name} style={{textTransform: "capitalize"}}>
                        {stat.stat.name}: <span style={{fontWeight:"bold", color:"#1976d2"}}>{stat.base_stat}</span>
                      </li>
                    ))}
                  </ul>
                </div>
              </>
            ) : (
              <p style={{fontWeight:"bold"}}>Erro ao carregar informações do Pokémon.</p>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
